function getAllLines() {
  let lines = []
  for (const line of sz_line_stations) {
    lines.push({
      name: line['line_name'],
      color: line['color']
    })
  }
  return lines
}

function getLineColor(line_index) {
  return sz_line_stations[line_index]['color']
}

function getLineStations(line_index) {
  let stations = []
  for (const station of sz_line_stations[line_index]['stations']) {
    stations.push(station['station_name'])
  }
  return stations
}

function getStationWcInfo(line_index, station_index) {
  return sz_line_stations[line_index]['stations'][station_index]['wc_info']
}

function calcLatLonDistance(lat1, lon1, lat2, lon2) {
  var dLat = (lat2 - lat1) * (Math.PI / 180);
  var dLon = (lon2 - lon1) * (Math.PI / 180);
  var lat1 = (lat1) * (Math.PI / 180);
  var lat2 = (lat2) * (Math.PI / 180);

  var a = Math.sin(dLat / 2) * Math.sin(dLat / 2) +
          Math.sin(dLon / 2) * Math.sin(dLon / 2) * Math.cos(lat1) * Math.cos(lat2);
  var c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
  const RADIUS_KM = 6378.137;
  return RADIUS_KM * c;
}

function getNearByStations(lat, lon, limit = 3, max_distance = 5) {
  let nearby_stations = []
  for (let line_index = 0; line_index < sz_line_stations.length; ++line_index) {
    const stations = sz_line_stations[line_index]['stations']
    for (let station_index = 0; station_index < stations.length; ++station_index) {
      const station = stations[station_index]
      const distance = calcLatLonDistance(lat, lon, station['latitude'], station['longitude'])
      if (distance <= max_distance) {
        nearby_stations.push({
          line_index,
          station_index,
          station_name: station['station_name'],
          wc_info: station['wc_info'],
          distance: Math.round(distance * 1000)
        })
      }
    }
  }

  // 按距离从小到大排序
  nearby_stations.sort((a, b) => {
    const diff = a.distance - b.distance
    if (Math.abs(diff) > 1e-6) {
      return diff
    }
    // 距离相同的，让更新（简单等价成线路数字更大）的路线排前边
    return b.line_index - a.line_index 
  })
  for (let i = 0; i < 10 && i < nearby_stations.length; ++i) {
    console.log(nearby_stations[i])
  }
  let result = []
  // 可换乘站点在多个线路上，需要去重一下
  for (let i = 0; i < nearby_stations.length && result.length < limit; ++i) {
    let duplicate = false
    for (let j = 0; j < result.length; ++j) {
      if (nearby_stations[i]['station_name'] === result[j]['station_name']) {
        duplicate = true
        break
      }
    }
    if (!duplicate) {
      result.push(nearby_stations[i])
    }
  }
  return result
}

module.exports = {
  getAllLines,
  getLineColor,
  getLineStations,
  getStationWcInfo,
  getNearByStations,
}